#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="E:\MY_COURSE\nodejs_lessons\mern_login_form\node_modules\.pnpm\handlebars@4.7.7\node_modules\handlebars\bin\node_modules;E:\MY_COURSE\nodejs_lessons\mern_login_form\node_modules\.pnpm\handlebars@4.7.7\node_modules\handlebars\node_modules;E:\MY_COURSE\nodejs_lessons\mern_login_form\node_modules\.pnpm\handlebars@4.7.7\node_modules;E:\MY_COURSE\nodejs_lessons\mern_login_form\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/e/MY_COURSE/nodejs_lessons/mern_login_form/node_modules/.pnpm/handlebars@4.7.7/node_modules/handlebars/bin/node_modules:/mnt/e/MY_COURSE/nodejs_lessons/mern_login_form/node_modules/.pnpm/handlebars@4.7.7/node_modules/handlebars/node_modules:/mnt/e/MY_COURSE/nodejs_lessons/mern_login_form/node_modules/.pnpm/handlebars@4.7.7/node_modules:/mnt/e/MY_COURSE/nodejs_lessons/mern_login_form/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../bin/handlebars" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../bin/handlebars" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../bin/handlebars" $args
  } else {
    & "node$exe"  "$basedir/../../bin/handlebars" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
